#Task 3
def my_func(a,b,c):
    if a >= b and b >= c :
        return a + b
    elif a > b and a < c :
        return a + c
    else:
       return  c + b
print(f'answer : {my_func(int(input("a: ")), int(input("b: ")), int(input("c: ")))}')

